 function whichFloorToGo (instructions)
    floor = 0
    instructionLength = string.len(instructions)
    for index = 1, instructionLength, 1 do
      indexCharchter = string.sub(instructions, index, index)
      if indexCharchter == "(" then
        floor = floor + 1
      else
        floor = floor -1
      end
    end
    print (floor)
    return floor
  end
  

assert (whichFloorToGo ("") == 0)
assert (whichFloorToGo ("(") == 1)
assert (whichFloorToGo (")") == -1)
assert (whichFloorToGo ("((") == 2)
assert (whichFloorToGo ("(()") == 1)
