function enterBasement (instructions)
  floor = 0
  instructionsLength = string.len(instructions)
  for index = 1, instructionsLength, 1 do
    indexCharachter = string.sub(instructions, index, index)
    if indexCharachter == ")" then
      floor = floor - 1
    else
      floor = floor + 1
    end
    if floor == -1 then
      return index
    end
  end
end


