func whichFloor(instruction string) (floorNumber int) {
	floorNumber = 0
	for index := 0; index < len(instruction); index++ {
		indexCharachter := instruction[index]
		if indexCharachter == '(' {
			floorNumber = floorNumber + 1
		} else {
			floorNumber = floorNumber - 1
		}
	}
	return
}
